void init()
{
    allegro_init();
    set_color_depth(desktop_color_depth());
    if(set_gfx_mode(GFX_AUTODETECT_WINDOWED,800,600,0,0)!=0)
    {
        allegro_message("prblm gfx");
        allegro_exit();
        exit(EXIT_FAILURE);
    }
    install_keyboard();
    install_mouse();
}

SAMPLE * importeSon(char *nomDeFichier)
{
    SAMPLE *sonARendre= load_sample(nomDeFichier);
    if(!sonARendre)
    {
        allegro_message("ne peut pas ouvrir %s",nomDeFichier);
        allegro_exit();
        exit(EXIT_FAILURE);
    }
    return sonARendre;
}


BITMAP * importeImage(char *nomDeFichier)
{
    BITMAP *imageARendre= load_bitmap(nomDeFichier,NULL);
    if(!imageARendre)
    {
        allegro_message("ne peut pas ouvrir %s",nomDeFichier);
        allegro_exit();
        exit(EXIT_FAILURE);
    }
    return imageARendre;
}

void drawTile(BITMAP*buffer, BITMAP *image, int x, int y)
{
    x_screen = x_start + (x - y) * TILE_WIDTH/2;
    y_screen = y_start + (x + y) * TILE_HEIGHT/2;
    draw_sprite(buffer,image,x_screen,y_screen);
}


void drawWallTile(BITMAP*buffer, BITMAP *image, int x, int y)
{
    x-=1;
    y-=1;
    x_screen = x_start + (x - y) * TILE_WIDTH/2;
    y_screen = y_start + (x + y) * TILE_HEIGHT/4;
    draw_sprite(buffer,image,x_screen,y_screen);
}

void drawMap(BITMAP*buffer, BITMAP **image)
{
    for (int i = 0; i < GRID_SIZE; i++) {
        for (int j = 0; j < GRID_SIZE; j++) {
            if(mapF[i*GRID_SIZE+j]>-1)
            {
                drawTile(buffer,image[mapF[i*GRID_SIZE+j]], i, j);
            }

        }
    }
}
void button(Vector2D *vOrigin)
{
    if(key[KEY_UP])
    {
        vOrigin->y++;
    }
    if(key[KEY_DOWN])
    {
        vOrigin->y--;
    }
    if(key[KEY_LEFT])
    {
        vOrigin->x++;
    }
    if(key[KEY_RIGHT])
    {
        vOrigin->x--;
    }
}

int myClock(int diffAfterReset)
{
    return clock() - diffAfterReset; // Retourne le temps depuis le dernier reset
}

void myResetClock( int * diffAfterReset)
{
    *diffAfterReset = clock(); // Met le compteur au véritable temps
}



